import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('student'); // Default role set to 'student'
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  // const handleLogin = async (e) => {
  //   e.preventDefault();

  //   const response = await fetch('http://localhost:3001/login', {
  //     method: 'POST',
  //     headers: {
  //       'Content-Type': 'application/json',
  //     },
  //     body: JSON.stringify({ username, password, role }), // Include the role in the request body
  //   });

  //   const data = await response.json();
  //   if (data.success) {
  //     setMessage('Login successful!');
  //     // Redirect based on role using navigate
  //     if (data.role === 'admin') {
  //       setMessage('Welcome to Admin Dashboard');
  //       navigate('./admin-dashboard');
  //     } else if (data.role === 'superadmin') {
  //       setMessage('Welcome to Super Admin Dashboard');
  //       navigate('./superadmin-dashboard');
  //     } else if (data.role === 'student') {
  //       setMessage('Welcome to Student Dashboard');
  //       navigate('./student-dashboard');
  //     }
  //   } else {
  //     setMessage('Invalid credentials');
  //   }
  // };
  const handleLogin = async (e) => {
    e.preventDefault(); // Prevent default form submission

    const response = await fetch('http://localhost:3001/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username, password, role }), // Include the role in the request body
    });

    if (!response.ok) {
      setMessage('Server error, please try again later.');
      return;
    }

    try {
      const data = await response.json(); // Parse the JSON response
      if (data.success) {
        setMessage('Login successful!');

        // Prepare welcome message based on role
        let welcomeMessage = '';
        if (data.role === 'admin') {
          welcomeMessage = 'Welcome to Admin Dashboard';
        } else if (data.role === 'superadmin') {
          welcomeMessage = 'Welcome to Super Admin Dashboard';
        } else if (data.role === 'student') {
          welcomeMessage = 'Welcome to Student Dashboard';
        }

        setMessage(welcomeMessage); // Show welcome message

        // Set a timeout to navigate after displaying the message
        setTimeout(() => {
          if (data.role === 'admin') {
            navigate('./admin-dashboard'); // Navigate to admin dashboard
          } else if (data.role === 'superadmin') {
            navigate('./superadmin-dashboard'); // Navigate to superadmin dashboard
          } else if (data.role === 'student') {
            navigate('./student-dashboard'); // Navigate to student dashboard
          }
        }, 2000); // Navigate after 2 seconds (2000 milliseconds)
      } else {
        setMessage('Invalid credentials'); // Show error message if login fails
      }
    } catch (error) {
      console.error('Error parsing JSON:', error);
      setMessage('Failed to parse server response.'); // Show error if JSON parsing fails
    }
  };


  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <div>
          <label>Username:</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div>
          <label>User Role:</label>
          <select
            value={role}
            onChange={(e) => setRole(e.target.value)}
            required
          >
            <option value="admin">Admin</option>
            <option value="superadmin">Super Admin</option>
            <option value="student">Student</option>
          </select>
        </div>
        <button type="submit">Login</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}

export default Login;
